const _day_lookup = {}

function _get_day (day_name) {
  if (_day_lookup[day_name] === undefined) {
    _day_lookup[day_name] = new TzolkinDay(day_name)
  }
  return _day_lookup[day_name]
}

/**
 * Describes only the day component of a 260-day cycle
 */
class TzolkinDay {
  /**
   * @param {string} name - Name of the 260-day cycle day
   */
  constructor (name) {
    /**
     * Name of the day in the 260-day cycle
     * @type {string}
     */
    this.name = name

    /**
     * Mapping of day names to indexes
     * @type {Map<number, string>}
     */
    this.days = [
      undefined,
      'Imix',
      'Ik\'',
      'Ak\'bal',
      'K\'an',
      'Chikchan',
      'Kimi',
      'Manik',
      'Lamat',
      'Muluk',
      'Ok',
      'Chuwen',
      'Eb',
      'Ben',
      'Ix',
      'Men',
      'Kib',
      'Kaban',
      'Etz\'nab',
      'Kawak',
      'Ajaw',
    ]

    this.day_position = this.days.findIndex(
      d => d === this.name)
  }

  /**
   * Return the next day in the 260-day cycle
   * @returns {TzolkinDay}
   */
  next () {
    let i = this.day_position + 1
    if (i > 20) {
      i = 1
    }
    return _get_day(this.days[i])
  }
}

module.exports = {
  'TzolkinDay': TzolkinDay,
  'Tzolkin': Tzolkin,
}

/**
 * An abstract class to handle the create of an object from a string
 */
class Factory {
  /**
   * Define properties to be override by sub-classes
   */
  constructor () {
    /**
     * Describes how to break a string into a date element
     * @protected
     * @type {RegExp}
     */
    this.pattern = null
  }

  /**
   * Split the provided date into its components
   * @param {string} raw
   * @access protected
   * @returns {String[]}
   */
  _split (raw) {
    let matches = raw.match(
      this.pattern,
    )
    if (matches === null) {
      return []
    }
    return matches.slice(1)
  }

  /**
   * Checks if the string contains a partial date
   * @param {string} raw - Raw date string
   * @access protected
   * @returns {boolean}
