/** @ignore */
const tzolkin = require('../cr/tzolkin');
/** @ignore */
const haab = require('../cr/haab');
/** @ignore */
const wildcard = require('../wildcard');

/**
 * A combination of 260-day cycles and the Haab cycle.
 * @example
 *  let cr = new CalendarRound(4, "Ajaw", 8, "Kumk'u");
 */
class CalendarRound {
  /**
   *
   * @param {number} tzolkinCoeff Coefficient for the 260-day cycle
   * @param {string} tzolkinDay Name of the name in the 260-day cycle
   * @param {number} haabCoeff Day in the Haab month
   * @param {string} haabMonth Name of the Haab month
   */
  constructor(tzolkinCoeff, tzolkinDay, haabCoeff, haabMonth) {
    /**
     * 260-day cycle component of the Calendar Round
     * @type {Tzolkin}
     */
    this.tzolkin = tzolkin.getTzolkin(tzolkinCoeff, tzolkinDay);
    /**
     * Haab cycle component of the Calendar Round
     * @type {Haab}
     */
    this.haab = haab.getHaab(haabCoeff, haabMonth);

    this.validate();
  }

  /**
   * Validate that the Calendar Round has a correct 260-day and Haab
   * configuration
   */
  validate() {
    let validHaabCoeffs = [];
    if ([
      'Kaban', 'Ik\'', 'Manik\'', 'Eb',
    ].includes(this.tzolkin.name)) {
      validHaabCoeffs = [0, 5, 10, 15];
    } else if ([
      'Etz\'nab', 'Ak\'bal', 'Lamat', 'Ben',
    ].includes(this.tzolkin.name)) {
      validHaabCoeffs = [1, 6, 11, 16];
    } else if ([
      'Kawak', 'K\'an', 'Muluk', 'Ix',
    ].includes(this.tzolkin.name)) {
      validHaabCoeffs = [2, 7, 12, 17];
    } else if ([
      'Ajaw', 'Chikchan', 'Ok', 'Men',
    ].includes(this.tzolkin.name)) {
      validHaabCoeffs = [3, 8, 13, 18];
    } else if ([
      'Imix', 'Kimi', 'Chuwen', 'Kib',
    ].includes(this.tzolkin.name)) {
      validHaabCoeffs = [4, 9, 14, 19];
    } else if ([wildcard].includes(this.tzolkin.name)) {
      validHaabCoeffs = [...Array(19).keys()];
    } else {
      throw new Error(`Could not allocate Tzolk'in (${this.tzolkin.name}) to permissible month coeffs.`);
    }

    validHaabCoeffs.push(wildcard);

    if (!validHaabCoeffs.includes(this.haab.coeff)) {
      throw new Error(`${this} should have Haab coeff in ${validHaabCoeffs} for day ${this.tzolkin.name}`);
    }
  }

  /**
   * Increment both the Haab and 260-day cycle to the next day in the Calendar Round
   * @returns {CalendarRound}
   */
  next() {
    const newTzolkin = this.tzolkin.next();
    const newHaab = this.haab.next();
    return getCalendarRound(
      newTzolkin.coeff,
      newTzolkin.day,
      newHaab.coeff,
      newHaab.month,
    );
  }

  /**
   * Check that this CalendarRound matches another CalendarRound. If one CR has
   * wildcards and the other does not, this function will return false.
   * @param {CalendarRound} newCr
   * @return {Boolean}
   */
  equal(newCr) {
    return this.haab.equal(newCr.haab)
      && this.tzolkin.equal(newCr.tzolkin);
  }

  /**
   * Check that this Calendar Round matches another CalendarRound. If one CR has
   * wildcards and the other does not, this function will return true.
   * @param {CalendarRound} newCr
   * @return {boolean}
   */
  match(newCr) {
    const haabMatches = this.haab.match(newCr.haab);
    const tzolkinMatches = this.tzolkin.match(newCr.tzolkin);
    return haabMatches && tzolkinMatches;
  }

  /**
   * Shift a CalendarRound fullDate forward through time. Does not modify this
   * object and will return a new object.
   * @param {number} increment
   * @return {CalendarRound}
   */
  shift(increment) {
    const newHaab = this.haab.shift(increment);
    const newTzolkin = this.tzolkin.shift(increment);
    return getCalendarRound(
      newTzolkin.coeff,
      newTzolkin.day,
      newHaab.coeff,
      newHaab.month,
    );
  }

  /**
   * Return true, if this function has any wildcard portions.
   * @return {boolean}
   */
  isPartial() {
    return (this.tzolkin.day === wildcard)
      || (this.tzolkin.coeff === wildcard)
      || (this.haab.month === wildcard)
      || (this.haab.coeff === wildcard);
  }

  /**
   * Render the CalendarRound cycle fullDate as a string
   * @returns {string}
   */
  toString() {
    return `${this.tzolkin} ${this.haab}`;
  }
}

/** @ignore */
const singleton = {};

/**
 * Return a comparable instance of a Calendar Round.
 * @param {number} tzolkinCoeff
 * @param {HaabMonth|string} tzolkinDay
 * @param {number} haabCoeff
 * @param {HaabMonth|string} haabMonth
 * @return {CalendarRound}
 */
function getCalendarRound(tzolkinCoeff, tzolkinDay, haabCoeff, haabMonth) {
  const crId = `${tzolkinCoeff} ${tzolkinDay} ${haabCoeff} ${haabMonth}`;
  if (singleton[crId] === undefined) {
    singleton[crId] = new CalendarRound(tzolkinCoeff, tzolkinDay, haabCoeff, haabMonth);
  }
  return singleton[crId];
}

module.exports = getCalendarRound;

/** @ignore */
const months = [
  undefined,
  'Pop',
  'Wo',
  'Sip',
  'Sotz\'',
  'Sek',
  'Xul',
  'Yaxk\'in',
  'Mol',
  'Ch\'en',
  'Yax',
  'Sak',
  'Keh',
  'Mak',
  'K\'ank\'in',
  'Muwan',
  'Pax',
  'K\'ayab',
  'Kumk\'u',
  'Wayeb',
];

/** @ignore */
const monthIndices = {
  undefined: 0,
  Pop: 1,
  Wo: 2,
  Sip: 3,
  'Sotz\'': 4,
  Sek: 5,
  Xul: 6,
  'Yaxk\'in': 7,
  Mol: 8,
  'Ch\'en': 9,
  Yax: 10,
  Sak: 11,
  Keh: 12,
  Mak: 13,
  'K\'ank\'in': 14,
  Muwan: 15,
  Pax: 16,
  'K\'ayab': 17,
  'Kumk\'u': 18,
  Wayeb: 19,
};

/** @ignore */
const singleton = {};

/**
 * Return a comparable HaabMonth instantiation.
 * @param name
 * @return {HaabMonth}
 */
function getHaabMonth(name) {
  const monthName = (typeof name === 'number') ? months[name] : name;
  if (singleton[monthName] === undefined) {
    // eslint-disable-next-line no-use-before-define
    singleton[monthName] = new HaabMonth(monthName);
  }
  return singleton[monthName];
}

/**
 * Describes only the month component of a Haab fullDate
 */
class HaabMonth {
  /**
   * @param {string} name - Name of the Haab month
   */
  constructor(name) {
    /**
     * Name of the Haab month
     * @type {string}
     */
    this.name = name;

    /**
     * @type {number}
     */
    this.month_position = monthIndices[this.name];
  }

  /**
   * Return the next month in the Haab cycle
   * @returns {HaabMonth}
   */
  next() {
    return this.shift(1);
  }

  /**
   * Ensure a Haab month name is defined, and that the month name is within the
   * set of allowable values.
   */
  validate() {
    if (this.name === undefined) {
      throw new Error('Haab\' month name must be provided');
    }
    if (!months.includes(this.name)) {
      throw new Error(`Haab' day (${this.name}) must be in ${months}`);
    }
  }

  /**
   * Shift a HaabMonth fullDate forward through time. Does not modify this
   * object and will return a new object.
   * @param {number} increment - Number of months to move forward
   * @return {HaabMonth}
   */
  shift(increment) {
    let newIncremental = (this.month_position + increment) % 19;
    newIncremental = (newIncremental === 0) ? 19 : newIncremental;
    return getHaabMonth(newIncremental);
  }

  /**
   * Render this month as a string
   * @return {string}
   */
  toString() {
    return this.name;
  }
}


module.exports = {
  HaabMonth,
  getHaabMonth,
};

/** @ignore */
const wildcard = require('../wildcard');
/** @ignore */
const {getHaabMonth} = require('./haab-month');

/** @ignore */
const singleton = {};

/**
 * Return a comparable HaabMonth instantiation.
 * @param {number} coeff
 * @param {HaabMonth|string} month
 * @return {Haab}
 */
function getHaab(coeff, month) {
  const monthName = `${coeff} ${month}`;
  // const monthName = (typeof name === 'number') ? months[name] : name;
  if (singleton[monthName] === undefined) {
    // eslint-disable-next-line no-use-before-define
    singleton[monthName] = new Haab(coeff, month);
  }
  return singleton[monthName];
}

/**
 * Describes a Haab fullDate with a position and a month
 * @example
 *    let day = new Haab(8, "Kumk'u");
 *
 * @example
 *    let day = new Haab(8, new HaabMonth("Kumk'u"));
 *
 */
class Haab {
  /**
   * Constructor
   * @param {number|Wildcard|string} coeff - The position in the Haab month for this fullDate
   * @param {string|HaabMonth|Wildcard} month
   */
  constructor(coeff, month) {
    let newCoeff = coeff;
    if (coeff === '*') {
      newCoeff = wildcard;
    } else if (coeff !== wildcard) {
      newCoeff = parseInt(newCoeff, 10);
    }
    let newMonth = month;
    if (typeof month === 'string') {
      if (month === '*') {
        newMonth = wildcard;
      } else {
        newMonth = getHaabMonth(month);
        // newMonth = new HaabMonth(month);
      }
    }
    /**
     * @type {HaabMonth|Wildcard}
     */
    this.month = newMonth;
    /**
     * @type {number|Wildcard}
     */
    this.coeff = newCoeff;

    /**
     * Lazy loaded instance of the next Haab date in the cycle
     * @type {Haab}
     */
    this.private_next = undefined;

    this.validate();
  }

  /**
   * Ensure the Haab's coefficients are within range and the month is defined
   * @return {boolean}
   */
  validate() {
    if (this.coeff > 19 || this.coeff < 0) {
      throw new Error('Haab\' coefficient must inclusively between 0 and 19.');
    }
    if (this.month === getHaabMonth('Wayeb') && this.coeff > 4) {
      throw new Error('Haab\' coefficient for Wayeb must inclusively between 0 and 4.');
    }
    if (this.month === undefined) {
      throw new Error('Haab\' month must be provided');
    }
    if (this.month !== wildcard) {
      this.month.validate();
    }

    return true;
  }

  /**
   * Return the next day in the Haab cycle
   * @returns {Haab}
   */
  next() {
    return this.shift(1);
  }

  /**
   * Ensure this Haab object has the same configuration as another Haab object.
   * Does not take wildcards into account.
   * @param {Haab} newHaab
   * @return {boolean}
   */
  equal(newHaab) {
    return this === newHaab;
  }

  /**
   * Ensure this Haab object has a matching configuration as another Haab object.
   * Takes wildcards into account.
   * @param {Haab} newHaab
   * @return {boolean}
   */
  match(newHaab) {
    return (
      (this.coeff === wildcard || newHaab.coeff === wildcard)
        ? true
        : (this.coeff === newHaab.coeff)
    ) && (
      (this.month === wildcard || newHaab.month === wildcard)
        ? true
        : (this.name === newHaab.name)
    );
  }

  /**
   * Return a string representation of the Haab month name
   * @returns {string|Wildcard}
   */
  get name() {
    if (this.month === wildcard) {
      return this.month;
    }
    return this.month.name;
  }

  /**
   *
   * @param {number} newIncremental
   */
  shift(newIncremental) {
    const incremental = newIncremental % 365;
    if (this.private_next === undefined) {
      if (incremental > 0) {
        const monthLength = (this.month === getHaabMonth(19)) ? 5 : 20;
        if (1 + this.coeff >= monthLength) {
          const newMonth = this.month.shift(1);
          this.private_next = getHaab(0, newMonth);
        } else {
          this.private_next = getHaab(this.coeff + 1, this.month);
        }
      } else {
        return this;
      }
    }
    if (incremental === 0) {
      return this;
    }
    return this.private_next.shift(incremental - 1);
  }

  /**
   * Render the Haab fullDate as a string
   * @returns {string}
   */
  toString() {
    return `${this.coeff} ${this.name}`;
  }
}


module.exports = {
  getHaab,
  getHaabMonth,
};

