/**
 * Mapping of day names to indexes
 * @type {Map<number, string>
 * @ignore
 */
const days = [
  undefined,
  'Imix',
  'Ik\'',
  'Ak\'bal',
  'K\'an',
  'Chikchan',
  'Kimi',
  'Manik\'',
  'Lamat',
