/** @ignore */
const tzolkin = require('./tzolkin')
/** @ignore */
const haab = require('./haab')

/**
 * A combination of 260-day cycles and the Haab cycle.
 * @example
 *  let cr = new CalendarRound(8, "Ajaw", 4, "Kumk'u");
 */
class CalendarRound {
  /**
   *
   * @param {number} tzolkin_coeff Coefficient for the 260-day cycle
   * @param {string} tzolkin_day Name of the name in the 260-day cycle
   * @param {number} haab_coeff Day in the Haab month
   * @param {string} haab_month Name of the Haab month
   */
  constructor (tzolkin_coeff, tzolkin_day, haab_coeff, haab_month) {
    /**
     * 260-day cycle component of the Calendar Round
     * @type {Tzolkin}
     */
    this.tzolkin = new tzolkin.Tzolkin(tzolkin_coeff, tzolkin_day)
    /**
     * Haab cycle component of the Calendar Round
     * @type {Haab}
     */
    this.haab = new haab.Haab(haab_coeff, haab_month)
  }

  /**
   * Increment both the Haab and 260-day cycle to the next day in the Calendar Round
   * @returns {CalendarRound}
   */
  next () {
    let new_cr = new CalendarRound()
    new_cr.haab = this.haab.next()
    new_cr.tzolkin = this.tzolkin.next()
    return new_cr
  }

  /**
   * Render the CalendarRound cycle date as a string
   * @returns {string}
   */
  toString () {
    return `${this.tzolkin} ${this.haab}`
  }
}

module.exports = {
  'CalendarRound': CalendarRound,
  'tzolkin': tzolkin,
  'haab': haab,
}

/** @ignore */
const CalendarRound = require('./calendar-round').CalendarRound
/** @ignore */
const LongCount = require('./long-count').LongCount
/** @ignore */
const wildcard = require('./wildcard')

/**
 * An abstract class to handle the create of an object from a string
 */
class Factory {
  /**
   * Define properties to be override by sub-classes
   */
  constructor () {
    /**
     * Describes how to break a string into a date element
     * @protected
     * @type {RegExp}
     */
    this.pattern = null
  }

  /**
   * Split the provided date into its components
   * @param {string} raw
   * @access protected
   * @returns {String[]}
   */
  _split (raw) {
    let matches = raw.match(
      this.pattern,
    )
    if (matches === null) {
      return []
    }
    return matches.slice(1)
  }

  /**
   * Checks if the string contains a partial date
   * @param {string} raw - Raw date string
   * @access protected
   * @returns {boolean}
   */
  // _is_partial (raw) {
  //   let parts = this._split(raw)
  //   return parts.includes('*')
  // }
}

/**
 * A factory to create a CalendarRound object from a string
 * @extends Factory
 * @example
 *    let cr = new CalendarRoundFactory().parse("4 Ajaw 8 Kumk'u");
 */
class CalendarRoundFactory extends Factory {
  /**
   * Defines the pattern describing a Calendar Round
   */
  constructor () {
    super()
    /**
     * Describes how to break the string into a Calendar Round
     * @type {RegExp}
     */
    this.pattern = /^([*\d]+)\s?([^\s]+)\s?([*\d]+)\s?([^\s]+)$/
  }

  /**
   * Given a string, parse it and create a Calendar Round
   * @param {string} raw - A string containing a Calendar Round
   * @returns {CalendarRound}
   */
  parse (raw) {
    let parts = this._split(raw)
    return new CalendarRound(
      parseInt(parts[0]), parts[1],
      parseInt(parts[2]), parts[3],
    )
  }
}

/**
 * A factory to create a LongCount object from a string
 * @extends Factory
 * @example
 *    let cr = new LongCountFactory().parse("9.4.2.4.1");
 * @example
 *    let cr = new LongCountFactory().parse("9.4.2.*.1");
 */
class LongCountFactory extends Factory {
  /**
   * Given a string, parse it and create a Long Count
   * @param {string} raw - A string containing a Long Count
   * @returns {LongCount}
   */
  parse (raw) {
    let parts = raw.split('.')
    for (let i = 0; i < parts.length; i++) {
      if (parts[i] === '*') {
        parts[i] = wildcard
      } else {
        parts[i] = parseInt(parts[i])
      }
    }
    return new LongCount(...parts.reverse())
  }
}

module.exports = {
  'CalendarRoundFactory': CalendarRoundFactory,
  'LongCountFactory': LongCountFactory,
}

/**
 * Describes a Haab date with a position and a month
 * @example
 *    let day = new Haab(8, "Kumk'u");
 *
 * @example
 *    let day = new Haab(8, new HaabMonth("Kumk'u"));
 *
 */
class Haab {
  /**
   * Constructor
   * @param {number} coeff - The position in the Haab month for this date
   * @param {string|HaabMonth} month
   */
  constructor (coeff, month) {
    if (typeof month === 'string') {
      month = new HaabMonth(month)
    }
    /**
     * @type {HaabMonth}
     */
    this.month = month
    /**
     * @type {number}
     */
    this.coeff = coeff
  }

  /**
   * Return the next day in the Haab cycle
   * @returns {Haab}
   */
  next () {
    let month_length = (this.name === this.month.months[19]) ? 5 : 20
    let tomorrow_coeff = (this.coeff + 1) % month_length
    return new Haab(
      tomorrow_coeff,
      (tomorrow_coeff === 0) ? this.month.next() : this.month,
    )
  }

  /**
   * Return a string representation of the Haab month name
   * @returns {string}
   */
  get name () {
    return this.month.name
  }

  /**
   * Render the Haab date as a string
   * @returns {string}
   */
  toString () {
    return `${this.coeff} ${this.name}`
  }

}

/**
 * Describes only the month component of a Haab date
 */
class HaabMonth {
  /**
   * @param {string} name - Name of the Haab month
   */
  constructor (name) {
    /**
     * Name of the Haab month
     * @type {string}
     */
    this.name = name

    /**
     * @type {Map<number, string>}
     */
    this.months = {
      1: 'Pop',
      2: 'Wo',
      3: 'Sip',
      4: 'Sotz\'',
      5: 'Sek',
      6: 'Xul',
      7: 'Yaxk\'in',
      8: 'Mol',
      9: 'Ch\'en',
      10: 'Yax',
      11: 'Sak',
      12: 'Keh',
      13: 'Mak',
      14: 'K\'ank\'in',
      15: 'Muwan',
      16: 'Pax',
      17: 'K\'ayab',
      18: 'Kumk\'u',
      19: 'Wayeb',
    }
  }

  /**
   * Return the next month in the Haab cycle
   * @returns {HaabMonth}
   */
  next () {
    let i
    for (i = 0; i < 20; i++) {
      if (this.months[i] === this.name) {
        break
      }
    }
    i += 1
    if (i > 19) {
      i = 1
    }
    return new HaabMonth(this.months[i])
  }
}

module.exports = {
  'Haab': Haab,
  'HaabMonth': HaabMonth,
}

/**
 * Long Count cycle
 */
class LongCount {
  /**
   * @param {...number|Wildcard} cycles - Components in the long count (eg, K'in, Winal, Bak'tun, etc)
   */
  constructor (...cycles) {
    /**
     * Date Components
     * @type {number|Wildcard[]}
     */
    this.parts = cycles

    /**
     * Pattern to validate the date
     * @type {RegExp}
     */
    this.date_pattern = /([\d*]+\.?)+/

    /**
     * Convert individual components to a single string
     * @type {string}
     */
    this.raw = this.toString()
  }

  /**
   * Get specific column in Long Count date
   * @param {number} index
   * @returns {number}
   */
  _get_date_sections (index) {
    let part = this.parts[index]
    if (part === undefined) {
      return 0
    }
    return part
  }

  /**
   * Set specific column in Long Count date
   * @param {number} index
   * @param {number} value
   * @returns {LongCount}
   * @private
   */
  _set_date_sections (index, value) {
    this.parts[index] = value.toString()
    this.raw = this.toString()
    return this
  }

  /**
   * Set the k'in component of the date
   * @returns {number}
   */
  set k_in (new_k_in) {
    this._set_date_sections(0, new_k_in)
  }

  /**
   * Return the k'in component of the date
   * @returns {number}
   */
  get k_in () {
    return this._get_date_sections(0)
  }

  /**
   * Set the winal component of the date
   * @returns {number}
   */
  set winal (new_winal) {
    this._set_date_sections(1, new_winal)
  }

  /**
   * Return the winal component of the date
   * @returns {number}
   */
  get winal () {
    return this._get_date_sections(1)
  }

  /**
   * Set the tun component of the date
   * @returns {number}
   */
  set tun (new_tun) {
    this._set_date_sections(2, new_tun)
  }

  /**
   * Return the tun component of the date
   * @returns {number}
   */
  get tun () {
    return this._get_date_sections(2)
  }

  /**
   * Set the k'atun component of the date
   * @returns {number}
   */
  set k_atun (new_k_atun) {
    this._set_date_sections(3, new_k_atun)
  }

  /**
   * Return the k'atun component of the date
   * @returns {number}
   */
  get k_atun () {
    return this._get_date_sections(3)
  }

  /**
   * Set the bak'tun component of the date
   * @returns {number}
   */
  set bak_tun (new_bak_tun) {
    this._set_date_sections(4, new_bak_tun)
  }

  /**
   * Return the bak'tun component of the date
   * @returns {number}
   */
  get bak_tun () {
    return this._get_date_sections(4)
  }

  /**
   * Set the piktun component of the date
   * @returns {number}
   */
  set piktun (new_bak_tun) {
    this._set_date_sections(5, new_bak_tun)
  }

  /**
   * Return the piktun component of the date
   * @returns {number}
   */
  get piktun () {
    return this._get_date_sections(5)
  }

  /**
   * Set the kalabtun component of the date
   * @returns {number}
   */
  set kalabtun (new_bak_tun) {
    this._set_date_sections(6, new_bak_tun)
  }

  /**
   * Return the kalabtun component of the date
   * @returns {number}
   */
  get kalabtun () {
    return this._get_date_sections(6)
  }

  /**
   * Set the kinchiltun component of the date
   * @returns {number}
   */
  set kinchiltun (new_bak_tun) {
    this._set_date_sections(7, new_bak_tun)
  }

  /**
   * Return the kinchiltun component of the date
   * @returns {number}
   */
  get kinchiltun () {
    return this._get_date_sections(7)
  }

  /**
   * Ensure the date has only numbers and wildcards separated by points.
   * @returns {boolean}
   */
  is_valid () {
    return this.date_pattern.test(this.toString())
  }

  /**
   * Convert the LongCount to a string and pad the sections of the date
   * @returns {string}
   */
  toString () {
    let significant_digits = []
    for (let i = 0; i < this.parts.length; i++) {
      let part = this.parts[i]
      if (part !== 0) {
        significant_digits = this.parts.slice(i).reverse()
        break
      }
    }
    let date_length = significant_digits.length
    if (date_length < 5) {
      for (let i = 0; i < 5 - date_length; i++) {
        significant_digits.unshift(' 0')
      }
    }

    for (let i = 0; i < significant_digits.length; i++) {
      let part = significant_digits[i].toString()
      if (part.length < 2) {
        significant_digits[i] = ' ' + part
      }
    }
    return significant_digits.join('.')
  }
}

module.exports = {
  'LongCount': LongCount,
}

/**
 * Describes a date in the 260-day cycle with a position and a day
 * @example
 *    let day = new Tzolkin(4, "Ajaw");
 *
 * @example
 *    let day = new Tzolkin(4, new TzolkinDay("Ajaw"));
 *
 */
class Tzolkin {
  /**
   * Constructor
   * @param {number} coeff - The position in the 260-day cycle
   * @param {string|TzolkinDay} day
   */
  constructor (coeff, day) {
    if (typeof day === 'string') {
      day = new TzolkinDay(day)
    }
    /**
     * @type {TzolkinDay}
     */
    this.day = day
    /**
     * @type {number}
     */
    this.coeff = coeff
  }

  /**
   * Return the next day in the 260-day cycle
   * @returns {Tzolkin}
   */
  next () {
    let next_coeff = this.coeff + 1
    let next_day = this.day.next()
    return new Tzolkin(
      (next_coeff % 13) === 0 ? 13 : next_coeff % 13,
      next_day,
    )
  }

  /**
   * Return a string representation of the 260-day cycle name
   * @returns {string}
   */
  get name () {
    return this.day.name
  }

  /**
   * Render the 260-day cycle date as a string
   * @returns {string}
   */
  toString () {
    return `${this.coeff} ${this.name}`
  }
}

/**
 * Describes only the day component of a 260-day cycle
 */
class TzolkinDay {
  /**
   * @param {string} name - Name of the 260-day cycle day
   */
  constructor (name) {
    /**
     * Name of the day in the 260-day cycle
     * @type {string}
     */
    this.name = name

    /**
     * Mapping of day names to indexes
     * @type {Map<number, string>}
     */
    this.days = {
      1: 'Imix',
      2: 'Ik\'',
      3: 'Ak\'bal',
      4: 'K\'an',
      5: 'Chikchan',
      6: 'Kimi',
      7: 'Manik',
      8: 'Lamat',
      9: 'Muluk',
      10: 'Ok',
      11: 'Chuwen',
      12: 'Eb',
      13: 'Ben',
      14: 'Ix',
      15: 'Men',
      16: 'Kib',
      17: 'Kaban',
      18: 'Etz\'nab',
      19: 'Kawak',
      20: 'Ajaw',
    }
  }

  /**
   * Return the next day in the 260-day cycle
   * @returns {TzolkinDay}
   */
  next () {
    let i
    for (i = 0; i < 20; i++) {
      if (this.days[i] === this.name) {
        break
      }
    }
    i += 1
    if (i > 20) {
      i = 1
    }
    return new TzolkinDay(this.days[i])
  }
}

module.exports = {
  'TzolkinDay': TzolkinDay,
  'Tzolkin': Tzolkin,
}

/**
 * Describes a wildcard in the Calendar Round or Long Count
 */
class Wildcard {
  /**
   * Represent the Wildcard as a string
   * @returns {string}
   */
  toString () {
    return '*'
  }
}

module.exports = new Wildcard()

